
var mySwiper = new Swiper('.swiper-container', {
    // Optional parameters
    
    loop: true,

    // If we need pagination
    pagination: {
        el: '.swiper-pagination',
    },

    // Navigation arrows
    navigation: {
        nextEl: '.swiper-button-next',
        prevEl: '.swiper-button-prev',
    },

    // And if we need scrollbar
    scrollbar: {
        el: '.swiper-scrollbar',
    },
    
    slidesOffsetBefore: 50,
    spaceBetween: 50,/**スライダー同士の間隔 */
    slidesPerGroup: 1,
    autoHeight: true,/**slideの高さを自動調整してくれる　cssでheight調整無しで */
    autoplay: {/**自動スライド */
        delay: 5000,/**5秒後に自動でスライド */
        disableOnInteraction: false,
    },
    breakpoints: {
        // 768px以上の場合
        300: {
          slidesPerView: 1,
          spaceBetween: 10,/**スライダー同士の間隔 */
        },
        // 980px以上の場合
        750: {
          slidesPerView: 3,
          spaceBetween: 30,
        },
        // 1200px以上の場合
        1200: {
          slidesPerView: 4,
        }
      }


})



